package _03_numbers.java;

public class _02_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numa = 34;
		int numb = 36;

		int addNum = numb + numa;
		int subtractNum = numb - numa;
		int multiplyNum = numb * numa;
		int divideNum = numb / numa;
		int modulusNum = numb % numa;
		int incrementNum = ++numb;
		int decrementNum = --numb;
		
		
		System.out.println(addNum);
		System.out.println(subtractNum);
		System.out.println(multiplyNum);
		System.out.println(divideNum);
		System.out.println(modulusNum);
		System.out.println(incrementNum);
		System.out.println(decrementNum);		
		
		
	}

}
