package _08_Datacollection.java;

public class _01_Arrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int [] disArray = {
				100, 200, 300,
				400, 500, 600,
				700, 800, 900
		};
		System.out.println(disArray[8] + disArray[0]);
		
		String[] stringArray = { 
				"Carolyn", "Summer", "Jon",
				"Paul", "James", "Shane"
		};
		System.out.println(stringArray[0] + " " + stringArray[1]);
		
		String[] newArray = {
			"Gideon", "Zelda", "Dune",
			"Pepper", "Luna", "Bax" 
		};
		System.out.println(newArray[0] + " " + newArray[1] + " are the best babies");
	}

}
